import  React, {Component} from 'react';
import axios from 'axios';
import MedicineTableRow from './medicineTableRow';
import './css/adminHome.css';

export default  class viewMedicine extends  Component{


    constructor(props) {
        super(props);

        this.state = {medicine : [], search:''};
        // this.onChangeItemNo = this.onChangeItemNo.bind(this);
        // this.onChangeDate = this.onChangeDate.bind(this);
        // this.onChangeCategory = this.onChangeCategory.bind(this);
        // this.onChangeName = this.onChangeName.bind(this);
        // this.onChangeQuantity = this.onChangeQuantity.bind(this);
        // this.onChangeEDate = this.onChangeEDate.bind(this);
        // this.onChangeUPrice = this.onChangeUPrice.bind(this);
        // this.onChangeVender = this.onChangeVender.bind(this);
        // this.onChangeReOrderLevel = this.onChangeReOrderLevel.bind(this);
        // this.onSubmit = this.onSubmit.bind(this);

        // this.state = {
        //     itemno: '',
        //     date: '',
        //     category:'',
        //     name:'',
        //     qty:'',
        //     edate:'',
        //     uprice:'',
        //     vender:'',
        //     reorderlevel:''
        // }
    }

    componentDidMount() {
        // alert('email is ' +this.props.match.params.id);
        axios.get('http://localhost:4000/zooInventory/medicine/')
            .then(response => {
                this.setState({medicine : response.data});

            })
            .catch(function (error){
                console.log(error);
            })
    }

    tabRow(){
        return this.state.medicine.map(function (object, i){
            return <MedicineTableRow obj = {object} key = {i}/>;
        });
        
    }
    
    // onChangeItemNo(e){
    //     this.setState( {
    //         itemno: e.target.value
    //     });
    // }
    
    

    render() {
        return(
                <div>
                    <div class="sidebar">
                        <br/>
                        <center>
                            <h5 style={{fontWeight:'bold',color:'#edca2f'}}>INVENTORY DASHBOARD</h5>
                        </center>
                        <br/>
                        <a href = "">Admin Home Panel</a>
                        <a href = "/">Inventory Admin Home</a>
                        <a href = "/addFood">Add Foods</a>
                        <a href = "/addMedicine">Add Medicine</a>
                        <a href = "/addEquipment">Add Equipment</a>
                        <a href = "/">Suppliers</a>
                    </div>

                    <div class="content">
                        <div className = "top-tittle-bar">
                            <h2 className= 'tittle'>Medicine</h2>
                            <h6>Welcome to your medicine</h6>
                            <a href = "/addMedicine" className='btn btn-dark'>Go Back</a>
                        </div>
                        <h3 align="center">Medicine History</h3>
                        <hr/>
                        
                        <table className="table table-striped" style = {{marginTop :20}}>
                            <thead>
                                <tr>
                                    <th>Item No</th>
                                    <th>Date</th>
                                    <th>Name</th>
                                    <th>Quantity</th>
                                    <th>Expire Date</th>
                                    <th>Unit Price</th>
                                    <th>Vendor</th>
                                    <th>Re Order Level</th>
                                    <th colSpan="2">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.tabRow()}
                            </tbody>
                        </table>
                        

                            
                        <hr/>  
                        <hr/>
                        </div>
                </div>
        );
    }
}
