import  React, {Component} from 'react';
import axios from 'axios';

import './css/adminHome.css';

export default  class addFood extends  Component{


    constructor(props) {
        super(props);

        this.onChangeItemNo = this.onChangeItemNo.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onChangeCategory = this.onChangeCategory.bind(this);
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeQuantity = this.onChangeQuantity.bind(this);
        this.onChangeEDate = this.onChangeEDate.bind(this);
        this.onChangeUPrice = this.onChangeUPrice.bind(this);
        this.onChangeVender = this.onChangeVender.bind(this);
        this.onChangeReOrderLevel = this.onChangeReOrderLevel.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            itemno: '',
            date: '',
            category:'',
            name:'',
            qty:'',
            edate:'',
            uprice:'',
            vender:'',
            reorderlevel:''
        }
    }
    
    onChangeItemNo(e){
        this.setState( {
            itemno: e.target.value
        });
    }
    onChangeDate(e){
        this.setState( {
            date: e.target.value
        });
    }
    onChangeCategory(e){
        this.setState( {
            category: e.target.value
        });
    }
    onChangeName(e){
        this.setState( {
            station: e.target.value
        });
    }
    onChangeQuantity(e){
        this.setState( {
            station: e.target.value
        });
    }
    onChangeEDate(e){
        this.setState( {
            station: e.target.value
        });
    }
    onChangeUPrice(e){
        this.setState( {
            station: e.target.value
        });
    }
    onChangeVender(e){
        this.setState( {
            station: e.target.value
        });
    }
    onChangeReOrderLevel(e){
        this.setState( {
            station: e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
        const obj = {
            name : this.state.name,
            arrival : this.state.arrival,
            deparcher : this.state.deparcher,
            station : this.state.station
        };

       
       
                        axios.post('http://localhost:4000/trainFoodAdmin/adminaddtrain',obj)
                        .then(res => {
                            alert("Train Add Successfully");
                            this.setState({
                                name :'',
                                arrival :'',
                                deparcher :'',
                                station:''
                    
                            })
                            console.log(res.data)});
                        // this.props.history.push('/signIn');
                        window.location.replace('/adminviewtrain/'+this.state.station);
                    
    }
    
    

    render() {
        return(
                <div>
                    <div class="sidebar">
                        <br/>
                        <center>
                            <h5 style={{fontWeight:'bold',color:'#edca2f'}}>INVENTORY DASHBOARD</h5>
                        </center>
                        <br/>
                        <a href = "/">Admin Home Panel</a>
                        <a href = "/">Add Foods</a>
                        <a href = "/">Add Medicine</a>
                        <a href = "/">Add Equipment</a>
                        <a href = "/">Suppliers</a>
                    </div>

                    <div class="content">
                        <div className = "top-tittle-bar">
                            <h2 className= 'tittle'>DASHBOARD</h2>
                            <h6>Welcome to your dashboard</h6>
                        </div>
                        <br/>
                        <h3 align="center">Add New Food</h3>
                       
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <label>Train Name :</label>
                                <input type ="text" required  className="form-control" value={this.state.name} onChange = {this.onChangeName}/>
                            </div>
                            <div className="form-group">
                                <label>Arrival Time :</label>
                                <input type ="time" required className="form-control" value={this.state.arrival} onChange = {this.onChangeArrivalTime}/>
                            </div>
                            <div className="form-group">
                                <label>Deparcher :</label>
                                <input type ="time" required className="form-control" value={this.state.deparcher} onChange = {this.onChangeDeparcherTime}/>
                            </div>
                            <div className="form-group">
                                <label>Station :</label>
                                <input type ="text" readOnly value={this.state.station} onChange = {this.onChangeStation} className="form-control"/>
                            </div>
                        
                            <div className="form-group">
                                <input type = "submit" value = "Add Train" className="btn btn-dark"/>
                            </div>
                        </form>
                        

                            
                        <hr/>  
                        <br/>
                        <hr/>
                        </div>
                </div>
        );
    }
}
