import  React, {Component} from 'react';
import axios from 'axios';

import './css/adminHome.css';

export default  class adminHome extends  Component{


    constructor(props) {
        super(props);

        this.onChangeQuantity = this.onChangeQuantity.bind(this);
        this.onChangeReOrderLevel = this.onChangeReOrderLevel.bind(this);
        this.state = 
        {
            qty:'',
            reorderlevel:'',
            vegetableresult:''
        };
    }

    onChangeQuantity(e){
        this.setState( {
            qty: e.target.value
        });
    }
    onChangeReOrderLevel(e){
        this.setState( {
            reorderlevel: e.target.value
        });
    }

    componentDidMount() {
        const category = "Vegetables";
        axios.get('http://localhost:4000/zooInventory/vegetable/'+category)
            .then(res => {
                this.setState({
                    qty: res.data.qty,
                    reorderlevel: res.data.reorderlevel
                });
            })
            .catch(function (error){
                console.log("Can't Get Data");
            })
        
        // if (parseInt(this.state.qty) <= parseInt(this.state.reorderlevel)) {

        //     alert("Stock Level Low !!!"+this.state.qty);
        //     this.state.vegetableresult = "Stock Level Low !!!";

        // } else {

        //     alert("Stock Level High !!!" +this.state.qty);
        //     this.state.vegetableresult = "Stock Level High !!!";

        // }
       
    }

    

    
    render() {
        return(
                <div>
                    <div class="sidebar">
                        <br/>
                        <center>
                            <h5 style={{fontWeight:'bold',color:'#edca2f'}}>INVENTORY DASHBOARD</h5>
                        </center>
                        <br/>
                        <a href = "/">Admin Home Panel</a>
                        <a href = "/addFood">Add Foods</a>
                        <a href = "/addMedicine">Add Medicine</a>
                        <a href = "/addEquipment">Add Equipment</a>
                        <a href = "/">Suppliers</a>
                    </div>

                    <div class="content">
                        <div className = "top-tittle-bar">
                            <h2 className= 'tittle'>DASHBOARD</h2>
                            <h6>Welcome to your dashboard</h6>
                            {/* <from style ={{float:'right',display:'flex',gap:5}} onSubmit={this.onSubmit}>
                                <div className="form-group">
                                    <input type ="text" required value={this.state.search} onChange = {this.onChangeSearch} className="form-control"/>
                                </div>
                                <div className="form-group" style ={{float:'right'}}>
                                    <a href ={"/adminsearchorder/"+this.state.search+"/"+this.props.match.params.id} style ={{float:'right',background:'#313332',padding:7,borderRadius:5,color:'white',textDecoration:'none'}}>Search</a>
                                </div>
                            </from> */}
                        </div>    

                        <hr/> 

                        <h4>Foods</h4>
                        <br/>
                        <div className='food'>
                            <div className='food-inner'>
                                <h5>Vegetables</h5>
                                <br/>
                                <h6>Quantity - {this.state.qty}</h6>
                                <h4 s>{this.state.vegetableresult}</h4>
                            </div>
                            <div className='food-inner'>
                               <h6>Fruits</h6>
                            </div>
                            <div className='food-inner'>
                                <h6>Meats</h6>
                            </div>
                            <div className='food-inner'>
                                <h6>Fish</h6>
                            </div>
                            <div className='food-inner'>
                                <h6>Milk</h6>
                            </div>
                        </div> 

                        <hr/>

                        <h4>Medicine</h4>

                        <div>
                            
                        </div> 
                        <hr/>
                        </div>
                </div>
        );
    }
}
