    componentDidMount() {
        // alert('email is ' +this.props.match.params.id);
        axios.get('http://localhost:4000/zooInventory/food/')
            .then(response => {
                this.setState({food : response.data});

            })
            .catch(function (error){
                console.log(error);
            })
    }

